mapper.select:
select d.id, d.category_id, d.name, d.price, d.status, d.description, d.image, d.update_time, c.name as category_name, df.value as flavors from dish d left outer join category c on d.category_id=c.id left outer join dish_flavor df on d.id = df.dish_id
and d.name like concat('%'+#{name}+'%')
and d.category_id = #{categoryId}
and d.status = #{status}
order by d.update_time desc

mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status},
update_time = #{updateTime},
update_user = #{updateUser}
where id = #{id}
