mapper.select:
select s.id,s.category_id,s.name as name,s.price, s.status,s.description,s.image, s.update_time,c.name as category_name from setmeal s,category c
s.category_id=c.id
and s.name like concat('%'+#{name}+'%')
and c.category_id = #{categoryId}
and s.status = #{status}
order by s.update_time desc

select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}

mapper.update:
update setmeal
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status},
where id = #{id}
